	}
	a:after {
		content: '';
		display: block;
		position: absolute;
		bottom: -.5em;
		left: 50%;
		height: .2em;
		width: 0;
		background: transparent;
		transition: width .5s ease, background-color .5s ease, left .5s ease;
	}
	.link {
		&:hover {
			a {
				&:after {
				  width: 100%;
				  left: 0;
				  background: #ccc;
				}
			}
		}
	}
	.content {
		margin: 0 auto;
		display: flex;
		width: 35em;
	}
	.source {
