@import url('https://fonts.googleapis.com/css?family=EB+Garamond:400,500i,700&display=swap');
@import url('https://fonts.googleapis.com/css?family=Roboto:300,400&display=swap');
@import url('https://fonts.googleapis.com/css?family=Nanum+Gothic:400,700,800&display=swap');


$copy: #373737;
$main: #f4f4f4;
$second: #DCD0C0;
$third: #C0B283;

body {
	background: #fff;
	font-family: 'Nanum Gothic', sans-serif;
	margin: 0;
	padding: 0;
}

ul,
li {
	text-decoration: none;
	list-style-type: none;
	padding: 0;
	margin: 0;
}

@mixin absoluteCenter(){
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%,-50%);
}

* {
	box-sizing: border-box;
}

.container {
	width: 100%;
	height: auto;
	position: absolute;
	display: flex;
	flex-direction: column;
	font-size: 1vw;
	background-color: #fff;
	overflow: hidden;
}

section {
	position: relative;
	height: 55em;
	opacity: 0;
	margin: 1em;
	background-color: $main;
	&.active {
		opacity: 1;
		transition: opacity .25s ease-in .25s;
	}
}

.content {
	padding: 3em 5em;
}

.intro {
	.content {
		width: 100%;
		height: 100%;
		display: flex;	
		font-size: 1.75em;
	}
	.bg {
		width: 100%;
		height: 100%;
		opacity: .2;
		background-size: cover;
		background-position: bottom;
		background-repeat: no-repeat;	
		position: absolute;
		top: 0;
		left: 0;	
	}
	.subcopy {
		position: absolute;
	    left: 50%;
	    transform: translateX(-50%);
	    bottom: 2em;
	    font-size: .7em;
	}
	.intro-name {
		@include absoluteCenter();
		width: 12em;
		height: 10em;
	}
	.first,
	.last {
		overflow: hidden;
		font-size: 3em;
		text-align: justify;
		background-color: rgb(246, 246, 246);
		position: relative;
		color: $copy;
	}
	.first {
		letter-spacing: .15em;
		top: 1.2em;
	}
	.last {
		top: -1.2em;
	}
	.divider {
		position: relative;
		z-index: 5;
		width: 100%;
		height: .3em;
		border-bottom: .3em solid #c0b283;
		&:before,
		&:after {
			content: '';
			position: absolute;
			background-color: rgb(246, 246, 246);
			height: 4em;
			width: 100%;
		}
		&:before {
			bottom: -.25em;
		}
		&:after {
			top: .25em;
		}
	}
	&.active {
		.first,
		.last {
			top: 0;
			transition: top 1s ease-in .25s;
		}
		.divider {
			&:before,
			&:after {
				// top: 0;
				height: 0;
				transition: height 1s ease-in .25s;
			}
		}
	}
	.title {
		flex: 1;
		font-weight: 700;
	}
	.intro-copy {
		flex: 1;
		text-align: left;
	}
}

.about {
	background-color: $second;
	.content {
		text-align: center;
		margin: 0 auto;
	}
	.about-copy {
		font-size: 1.5em;
		line-height: 2em;
		width: 75%;
		height: auto;
		@include absoluteCenter();
	}
	.craft,
	.about {
		margin: 1em auto;
	}
}

