			grid-template-rows: 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr;
			grid-gap: 1em;
			display: grid;
			margin: 0 auto;
		  	.project {
		  		position: relative;
		  		overflow: hidden;
		  		opacity: 0;
				&:nth-of-type(1) {
					background: #DADADA;
					grid-area: 7 / 7 / 11 / 10;
				}
				&:nth-of-type(2) {
					background: #313131;
					grid-area: 1 / 4 / 5 / 7;
				}
				&:nth-of-type(3) {
					background: #696969;
					grid-area: 1 / 7 / 4 / 10;
				}
				&:nth-of-type(4) {
					background: #D3D3D3;
					grid-area: 3 / 1 / 7 / 4;
				}
				&:nth-of-type(5) {
					background: #DCDCDC;
					grid-area: 5 / 4 / 8 / 7;
				}
				&:nth-of-type(6) {
					background: #B0B0B0;
					grid-area: 8 / 4 / 11 / 7;
				}
				&:nth-of-type(7) {
					background: #4A4A4A;
					grid-area: 7 / 1 / 11 / 4;
				}
				&:nth-of-type(8) {
					background: #F5F5F5;
					grid-area: 1 / 1 / 3 / 4;
				}
				&:nth-of-type(9) {
					background: #AAAAAA;
					grid-area: 4 / 7 / 7 / 10;
				}
				.front,
				.back {
					width: 100%;
					height: 100%;
					position: absolute;
					top: 0;
					left: 0;
				}
				.hero {
					width: 100%;
					height: 100%;
					position: relative;
					top: 0;
					left: 0;
					background-size: cover;
					background-repeat: no-repeat;
					background-position: 50%;
					filter: grayscale(100%);
					opacity: .3;
				}
				.back {
					opacity: 0;
					.back-copy {
						width: 100%;
						height: 100%;
						position: relative;
						font-size: 5.75em;
						text-align: left;
						margin-left: .2em;
						// transform: rotate(45deg);
					}
				}

				&.selected {
					.hero {
						filter: grayscale(0);
						opacity: .5;
					}
					.front {
						opacity: 0;
						transition: opacity .5s ease-in;
					}
					.back {
						opacity: 1;
						transition: opacity .5s ease-in;
						.back-copy {
							opacity: .5;					
						}
					}
				}
		  	}
		}
	}
	.panel-2 {
		color: #fff;
		.back-button {
			position: absolute;
			border-radius: 50%;
		    left: calc(100% - 8em);
		    z-index: 5;
		    height: 3.5em;
		    width: 3.5em;
		    top: 4em;
			background: rgba(255,255,255, 1) url('../dist/images/back.png') no-repeat;
		    background-size: 50%;
		    background-position: 50%;
		    background-repeat: no-repeat;
		    box-shadow: 0.3em 0.3em 0.3em #000;
		}
		.panel-2-inner {
			background-image: linear-gradient(to left, #323232, #000000);
			background: #323232;
			width: calc(100% - 8em);
			height: 100%;
			position: relative;
			left: 8em;
			box-sizing: border-box;
			padding: 2.5em;
		}
		.project-content {
			display: flex;
			width: 96%;
			height: 100%;
			font-size: 2em;
		}
		.label {
			font-family: 'EB Garamond', serif;
			margin-right: 1em;
			text-align: left;
			display: inline-block;
			width: 5em;
			font-weight: 700;

		}
		.left-col,
		.right-col {
			flex: 1;
			height: 100%;
		}
		.left-col {
			.title {
				font-size: 1.8em;
				line-height: 1em;
				margin-bottom: .5em;
				letter-spacing: .1em;
				text-transform: lowercase;
			}

			.client,
			.team {
				font-size: .7em;
				font-family: 'Roboto', sans-serif;
				font-weight: 300;
				text-transform: lowercase;
				em {
					color: #ccc;
					margin: 0 .2em;
					font-weight: 700;
					font-size: 1.2em;
				}
			}
			.team {
				top: 2em;
				position: relative;
			}
			.divider {
				width: 80%;
				height: .05em;
				border: .05em solid #fff;
				position: relative;
				top: 1em;
			}
			.description {
				position: relative;
				top: 6em;
				font-size: .6em;
				line-height: 1.6em;
			}
		}
		.right-col {
			flex-grow: 1.5;
			position: relative;
			.video {
				@include absoluteCenter();
				width: 100%;
				height: 50%;
			    transform: translate(-50%, -50%);
			}
			iframe {
			    width: 80%;
			    height: 95%;
			    margin: 0 auto;
			    background-size: cover;
			    background-position: 50%;
			    background-repeat: no-repeat;
			    opacity: 1;
			    box-shadow: 0.4em 0.4em 0.4em #000;
			    position: relative;
			    left: 50%;
			    transform: translateX(-50%);
			}
			.url {
				flex: 1;
				text-align: center;
				font-size: .75em;
				top: 1em;
				position: relative;
				a {
					color: #fff;
					text-decoration: none;
				}
			}
		}
	}
	&.active {
		.panel-1 {
			.grid {
				.project {
					opacity: 1;
					@for $i from 1 through 10 {
						&:nth-of-type(#{$i}) {
							transition: opacity .25s ease-in #{$i * .2}s;
						}
					}
				}
			}
		}
	}
}

.skills {
	background-color: $third;
	.content {
		font-size: 1.5em;
		width: 95%;
		height: 100%;
		margin: 0 auto;
	}
	.skills-title {
		text-align: left;
		font-size: 1.4em;
		// font-weight: 700;
	}
	.skills-list {
		// font-family: "Nanum Got", sans-serif;
		height: 14em;
		color: $copy;
		padding: 2em;
		display: grid;
		grid-template-columns: 1fr 1fr 1fr 1fr;
		grid-template-rows: 1fr 1fr 1fr;
		align-items: center;
	}
	.skill {
		height: 1em;
		flex: 1;
		text-align: left;
	}
}

.contact {
	background-color: $main;
	.content {
		margin: 0 auto;
		width: 45em;
	}
	.contact-me {
		text-align: center;
		display: block;
		font-size: 3em;
		margin-bottom: 3em;
	}
	.sources {
		display: flex;
	}
	.link {
		flex: 1;
		position: relative;
	}
	a {
		text-decoration: none;
		color: #c0b283;
		text-align: center;
		display: block;
	}
	a:after {
		content: '';
		display: block;
		position: absolute;
		bottom: -.5em;
		left: 50%;
		height: .2em;
		width: 0;
		background: transparent;
		transition: width .5s ease, background-color .5s ease, left .5s ease;
	}
	.link {
		&:hover {
			a {
				&:after {
				  width: 100%;
				  left: 0;
				  background: #ccc;
				}
			}
		}
	}

	.source {
		text-align: center;
		font-size: 2em;
	}
}

@media all and (max-width: 480px) {
	.container {
		font-size: 2vw;
	}
	section {
		height: 100em;
	}
	.intro {
		.bg {
			background-position: 0 0;
			transform: rotate(360deg);
		}
		.subcopy {
			width: 70%;
		}
		.intro-name {
			top: 20%;
		}
		.first,
		.last {
			background-color: rgb(240,240,240);
		}
		.divider {
			&:before,
			&:after {
				background-color: rgb(240,240,240);
			}
		}
	}
	.work {
		&.shrink {
			.panel-1 {
				.grid {
					left: calc(100% - 16.5em);
					width: 7em;
					.project {
						width: 6em;
						height: 6em;
					}
				}
			}
		}
		.panel-1 {
			.grid {
				display: block;
				.project {
					display: inline-block;
					width: 50%;
					height: calc(100% / 6);
				}
			}
		}
		.panel-2 {
			.panel-2-inner {
				width: calc(100% - 3em);
				.project-content {
					flex-direction: column;
				    font-size: 2em;
				    height: 90%;
				    top: 10%;
				    position: relative;
				}
				.left-col {
					font-size: 1.2em;
				}
			}
			.back-button {
				left: calc(100% - 6em);
			    z-index: 5;
			    height: 4.5em;
			    width: 4.5em;
			}
		}
	}
	.skills {
		.skills-list {
			height: 80%;
			display: block;
			margin: 1em 0;
		}
		.skill {
			width: 100%;
			height: calc(100% / 12);
		}
	}
}
